mapper.select:
SELECT user_id, email, name, nn, admin, reg_date
FROM user
WHERE email=#{email} AND password=#{password}

SELECT COUNT(*)
FROM user
WHERE email=#{email};

mapper.update:
UPDATE user
SET email=#{email}, name=#{name}, nn=#{nn}
WHERE user_id=#{userId}

mapper.select:
SELECT user_id, email, name, nn, admin, reg_date
FROM user
WHERE nn LIKE #{nn};
